select V.CODE INTERNAL_CODE, V.PROGRAM_FK as PROGRAM_CODE, VRP.REGISTRATION_CODE as REGISTRATION_CODE, REG_LOC.LABEL as REGISTRATION_COUNTRY, VRP.START_DATE_TIME as REGISTRATION_START_DATE, VRP.END_DATE_TIME as REGISTRATION_END_DATE, QV.NAME AS BOAT_TYPE, VF.NAME, VF.ADMINISTRATIVE_POWER, VF.GROSS_TONNAGE_GT, VF.LOA, VF.CONSTRUCTION_YEAR, PORT.LABEL as LANDING_LOCATION_LABEL, PORT.NAME as LANDING_LOCATION_NAME, V.PROGRAM_FK from VESSEL V inner join VESSEL_REGISTRATION_PERIOD VRP on VRP.vessel_fk = V.code inner join LOCATION REG_LOC on REG_LOC.ID = VRP.REGISTRATION_LOCATION_FK left outer join VESSEL_FEATURES VF on VF.VESSEL_FK=V.code and VF.start_date_time=VRP.start_date_time left outer join LOCATION PORT on PORT.ID = VF.BASE_PORT_LOCATION_FK left outer join VESSEL_PHYSICAL_FEATURES VPF on VPF.VESSEL_FK=V.code and VPF.start_date=VRP.start_date_time left outer join VESSEL_PHYSICAL_MEASUREMENT VPM on VPM.VESSEL_PHYSICAL_FEATURES_FK = VPF.ID and VPM.PMFM_FK=230 left outer join QUALITATIVE_VALUE QV on QV.ID = VPM.QUALITATIVE_VALUE_FK order by VRP.REGISTRATION_CODE, VRP.START_DATE_TIME